import React from 'react';
import './SudokuGrid.css'; // Make sure to include your CSS file
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRightIcon from '@mui/icons-material/KeyboardArrowRight';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';

const ComparisonSudokuCell = ({
  cellData,
  subgridIndex,
  manualPencilMode,
  manualPencilColors,
  manualPencilMarks,
  handleCellClick,
  handlePencilMarksChange,
  handleCellChange,
  showPencilMarks,
  isOriginalCell,
  isUserCell,
  pencilMarks,
  generatedCellValue,
  selectedColor,
  applyColorToCell,
}) => {
  const { value, symbol } = cellData;

  const renderArrowIcon = (direction) => {
    const iconStyle = {
      transform:
        direction === '^'
          ? 'rotate(0deg)'
          : direction === 'v'
          ? 'rotate(180deg)'
          : direction === '>'
          ? 'rotate(270deg)'
          : direction === '<'
          ? 'rotate(90deg)'
          : 'none',
    };

    return (
      <div className={`arrow-icon ${direction}`} style={iconStyle}>
        {direction === '^' && <KeyboardArrowUpIcon />}
        {direction === 'v' && <KeyboardArrowDownIcon />}
        {direction === '>' && <KeyboardArrowRightIcon />}
        {direction === '<' && <KeyboardArrowLeftIcon />}
      </div>
    );
  };

  return (
    <div
      className={`sudoku-cell-container ${isOriginalCell ? 'original-cell' : ''}`}
      onClick={handleCellClick}
    >
      {/* Render arrow icons on cell borders based on symbol */}
      {symbol && (
        <div className="arrow-icons-container">
          {symbol.includes('^') && renderArrowIcon('^')}
          {symbol.includes('v') && renderArrowIcon('v')}
          {symbol.includes('>') && renderArrowIcon('>')}
          {symbol.includes('<') && renderArrowIcon('<')}
        </div>
      )}

      {/* Rest of your existing code */}
      {manualPencilMode ? (
        <input
          id={`cell-${subgridIndex}`}
          type="text"
          className={`sudoku-cell subgrid-${subgridIndex} ${generatedCellValue !== '' ? 'generated-cell' : ''}`}
          style={{ color: manualPencilColors[subgridIndex] }}
          value={
            manualPencilMarks[subgridIndex].length > 0 &&
            manualPencilMarks[subgridIndex].map((mark) => (mark !== null ? mark : '')).join(' ')
          }
          placeholder=""
          onChange={(e) => handlePencilMarksChange(e, subgridIndex)}
          readOnly={isOriginalCell && isUserCell}
        />
      ) : (
        <input
          id={`cell-${subgridIndex}`}
          type="text"
          className={`sudoku-cell subgrid-${subgridIndex} ${generatedCellValue !== '' ? 'generated-cell' : ''}`}
          style={{ color: manualPencilColors[subgridIndex] }}
          value={
            manualPencilMarks[subgridIndex].map((mark) => (mark !== null ? mark : '')).join(' ')
          }
          placeholder={value !== '' ? value : ''}
          maxLength={1}
          onChange={(e) => handleCellChange(e, subgridIndex)}
          readOnly={isOriginalCell}
        />
      )}

      {showPencilMarks && generatedCellValue === '' && (
        <div className="pencil-marks">
          {pencilMarks.map((mark) => (
            <span key={mark} className="pencil-mark">
              {mark}
            </span>
          ))}
        </div>
      )}

    </div>
  );
};

export default ComparisonSudokuCell;
