import React from 'react';
import { Grid } from '@mui/material';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRightIcon from '@mui/icons-material/KeyboardArrowRight';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';

const ComparisonSudokuCell = ({ cellData }) => {
  const { value, up, down, left, right } = cellData;

  const getArrowIconRotation = (arrowValue) => {
    if (arrowValue === '<') {
      return 'rotate(90deg)';
    } else if (arrowValue === '>') {
      return 'rotate(-90deg)';
    } else if (arrowValue === '^') {
      return 'rotate(0deg)';
    } else if (arrowValue === 'v') {
      return 'rotate(180deg)';
    }
    return 'none';
  };

  return (
    <div className="comparison-sudoku-cell">
      <div className="symbol-container">
        {up && (
          <KeyboardArrowUpIcon
            className="arrow-icon"
            style={{
              transform: getArrowIconRotation(up),
            }}
          />
        )}
        {down && (
          <KeyboardArrowDownIcon
            className="arrow-icon"
            style={{
              transform: getArrowIconRotation(down),
            }}
          />
        )}
      </div>
      <div className="symbol-container">
        {left && (
          <KeyboardArrowLeftIcon
            className="arrow-icon"
            style={{
              transform: getArrowIconRotation(left),
            }}
          />
        )}
        {value}
        {right && (
          <KeyboardArrowRightIcon
            className="arrow-icon"
            style={{
              transform: getArrowIconRotation(right),
            }}
          />
        )}
      </div>
    </div>
  );
};

export default ComparisonSudokuCell;
