import React from 'react';
import { Grid } from '@mui/material';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRightIcon from '@mui/icons-material/KeyboardArrowRight';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';

const ComparisonSudokuCell = ({
  cellData,
  subgridIndex,
  manualPencilMode,
  manualPencilColors,
  manualPencilMarks,
  handleCellClick,
  handlePencilMarksChange,
  handleCellChange,
  showPencilMarks,
  isOriginalCell,
  isUserCell,
  pencilMarks,
  generatedCellValue,
  selectedColor,
  applyColorToCell,
}) => {
  const { value, symbols } = cellData;

  const getArrowIconRotation = (cellValue) => {
    if (cellValue === '<') {
      return 'rotate(180deg)';
    } else if (cellValue === '>') {
      return 'rotate(0deg)';
    } else if (cellValue === 'v') {
      return 'rotate(90deg)';
    } else if (cellValue === '^') {
      return 'rotate(270deg)';
    }
    return 'none';
  };

  const renderArrowIcon = (direction) => {
    const rotation = getArrowIconRotation(symbols[direction]);
    return (
      <div className={`arrow-icon arrow-${direction}`} style={{ transform: rotation }}>
        {symbols[direction] ? (
          <span className={`arrow-text ${direction}`}>{symbols[direction]}</span>
        ) : null}
      </div>
    );
  };

  return (
    <div className={`sudoku-cell-container ${isOriginalCell ? 'original-cell' : ''}`} onClick={handleCellClick}>
      {manualPencilMode ? (
        <input
          id={`cell-${subgridIndex}`}
          type="text"
          className={`sudoku-cell subgrid-${subgridIndex} ${generatedCellValue !== '' ? 'generated-cell' : ''}`}
          style={{ color: manualPencilColors[subgridIndex] }}
          value={
            manualPencilMarks[subgridIndex].length > 0 &&
            manualPencilMarks[subgridIndex].map((mark) => (mark !== null ? mark : '')).join(' ')
          }
          placeholder=""
          onChange={(e) => handlePencilMarksChange(e, subgridIndex)}
          readOnly={isOriginalCell && isUserCell}
        />
      ) : (
        <input
          id={`cell-${subgridIndex}`}
          type="text"
          className={`sudoku-cell subgrid-${subgridIndex} ${generatedCellValue !== '' ? 'generated-cell' : ''}`}
          style={{ color: manualPencilColors[subgridIndex] }}
          value={
            manualPencilMarks[subgridIndex].map((mark) => (mark !== null ? mark : '')).join(' ')
          }
          placeholder={value !== '' ? value : ''}
          maxLength={1}
          onChange={(e) => handleCellChange(e, subgridIndex)}
          readOnly={isOriginalCell}
        />
      )}

      {showPencilMarks && generatedCellValue === '' && (
        <div className="pencil-marks">
          {pencilMarks.map((mark) => (
            <span key={mark} className="pencil-mark">
              {mark}
            </span>
          ))}
        </div>
      )}

      <Grid container direction="column">
        <Grid item container justifyContent="space-between">
          {renderArrowIcon('up')}
          {renderArrowIcon('down')}
        </Grid>
        <Grid item container justifyContent="space-between">
          {renderArrowIcon('left')}
          {value}
          {renderArrowIcon('right')}
        </Grid>
      </Grid>
    </div>
  );
};

export default ComparisonSudokuCell;
