import React from 'react';
import { IconButton, Grid, makeStyles } from '@mui/material';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRightIcon from '@mui/icons-material/KeyboardArrowRight';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';

const useStyles = makeStyles((theme) => ({
  cell: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  symbolContainer: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    width: '100%',
  },
}));

const ComparisonSudokuCell = ({ cellData, onClick }) => {
  const classes = useStyles();
  const { value, symbol } = cellData;

  const getArrowIconRotation = (cellValue) => {
    if (cellValue === '>') {
      return 'rotate(90deg)';
    } else if (cellValue === 'v') {
      return 'rotate(180deg)';
    } else if (cellValue === '<') {
      return 'rotate(270deg)';
    }
    return 'none';
  };

  return (
    <div className={classes.cell} onClick={onClick}>
      <Grid container direction="column" alignItems="center">
        <Grid item className={classes.symbolContainer}>
          <KeyboardArrowUpIcon style={{ transform: getArrowIconRotation(symbol) }} />
        </Grid>
        <Grid item className={classes.symbolContainer}>
          <IconButton size="small">
            <KeyboardArrowLeftIcon style={{ transform: getArrowIconRotation(symbol) }} />
          </IconButton>
          {value}
          <IconButton size="small">
            <KeyboardArrowRightIcon style={{ transform: getArrowIconRotation(symbol) }} />
          </IconButton>
        </Grid>
        <Grid item className={classes.symbolContainer}>
          <KeyboardArrowDownIcon style={{ transform: getArrowIconRotation(symbol) }} />
        </Grid>
      </Grid>
    </div>
  );
};

export default ComparisonSudokuCell;
