import React from 'react';
import { Grid } from '@mui/material';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRightIcon from '@mui/icons-material/KeyboardArrowRight';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';

const ComparisonSudokuCell = ({
  cellData,
  manualPencilMode,
  manualPencilColors,
  manualPencilMarks,
  handlePencilMarksChange,
  handleCellChange,
  showPencilMarks,
  isOriginalCell,
  isUserCell,
  pencilMarks,
  generatedCellValue,
  selectedColor,
  applyColorToCell,
}) => {
  const { value, symbol } = cellData;

  const getArrowIconRotation = (cellValue) => {
    if (cellValue === '<') {
      return 'rotate(180deg)';
    } else if (cellValue === '>') {
      return 'rotate(0deg)';
    } else if (cellValue === 'v') {
      return 'rotate(90deg)';
    } else if (cellValue === '^') {
      return 'rotate(270deg)';
    }
    return 'none';
  };

  const getBorderColor = (cellValue) => {
    if (cellValue === '<') {
      return 'border-left-color: red;';
    } else if (cellValue === '>') {
      return 'border-right-color: green;';
    } else if (cellValue === 'v') {
      return 'border-bottom-color: blue;';
    } else if (cellValue === '^') {
      return 'border-top-color: orange;';
    }
    return '';
  };

  return (
    <div className="sudoku-cell" style={{ position: 'relative', borderColor: getBorderColor(symbol) }}>
      <Grid container direction="column">
        <Grid item container justifyContent="space-between">
          <KeyboardArrowUpIcon
            className="arrow-icon"
            style={{ transform: getArrowIconRotation('^'), position: 'absolute', top: -12, left: '50%' }}
          />
          <KeyboardArrowDownIcon
            className="arrow-icon"
            style={{ transform: getArrowIconRotation('v'), position: 'absolute', bottom: -12, left: '50%' }}
          />
        </Grid>
        <Grid item container justifyContent="space-between">
          <KeyboardArrowLeftIcon
            className="arrow-icon"
            style={{ transform: getArrowIconRotation('<'), position: 'absolute', left: -12, top: '50%' }}
          />
          {value}
          <KeyboardArrowRightIcon
            className="arrow-icon"
            style={{ transform: getArrowIconRotation('>'), position: 'absolute', right: -12, top: '50%' }}
          />
        </Grid>
      </Grid>
    </div>
  );
};

export default ComparisonSudokuCell;
