import React from 'react';
import { Grid, IconButton } from '@mui/material';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRightIcon from '@mui/icons-material/KeyboardArrowRight';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';

const ComparisonSudokuCell = ({
  cellData,
  manualPencilMode,
  manualPencilColors,
  manualPencilMarks,
  handlePencilMarksChange,
  handleCellChange,
  showPencilMarks,
  isOriginalCell,
  isUserCell,
  pencilMarks,
  generatedCellValue,
  selectedColor,
  applyColorToCell,
}) => {
  const { value, up, down, left, right } = cellData;

  const getArrowIconRotation = (arrowValue) => {
    if (arrowValue === '<') {
      return 'rotate(90deg)';
    } else if (arrowValue === '>') {
      return 'rotate(-90deg)';
    } else if (arrowValue === '^') {
      return 'rotate(0deg)';
    } else if (arrowValue === 'v') {
      return 'rotate(180deg)';
    }
    return 'none';
  };

  const getBorderColor = (arrowValue) => {
    if (arrowValue === '<') {
      return 'border-left-color: red;';
    } else if (arrowValue === '>') {
      return 'border-right-color: green;';
    } else if (arrowValue === 'v') {
      return 'border-bottom-color: blue;';
    } else if (arrowValue === '^') {
      return 'border-top-color: orange;';
    }
    return '';
  };

  return (
    <div className="sudoku-cell" style={{ position: 'relative', borderColor: getBorderColor(left) }}>
      <Grid container direction="column">
        <Grid item container justifyContent="space-between">
          <IconButton size="small">
            <KeyboardArrowUpIcon
              className="arrow-icon"
              style={{
                transform: getArrowIconRotation(up),
                position: 'absolute',
                top: -12,
                left: '50%',
              }}
            />
          </IconButton>
          <IconButton size="small">
            <KeyboardArrowDownIcon
              className="arrow-icon"
              style={{
                transform: getArrowIconRotation(down),
                position: 'absolute',
                bottom: -12,
                left: '50%',
              }}
            />
          </IconButton>
        </Grid>
        <Grid item container justifyContent="space-between">
          <IconButton size="small">
            <KeyboardArrowLeftIcon
              className="arrow-icon"
              style={{
                transform: getArrowIconRotation(left),
                position: 'absolute',
                left: -12,
                top: '50%',
              }}
            />
          </IconButton>
          {value}
          <IconButton size="small">
            <KeyboardArrowRightIcon
              className="arrow-icon"
              style={{
                transform: getArrowIconRotation(right),
                position: 'absolute',
                right: -12,
                top: '50%',
              }}
            />
          </IconButton>
        </Grid>
      </Grid>
    </div>
  );
};

export default ComparisonSudokuCell;
