import React from 'react';
import { Grid } from '@mui/material';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRightIcon from '@mui/icons-material/KeyboardArrowRight';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';

const ComparisonSudokuCell = ({ cellData }) => {
  const { value, up, down, left, right } = cellData;

  const getArrowIconRotation = (arrowValue) => {
    if (arrowValue === '<') {
      return 'rotate(90deg)';
    } else if (arrowValue === '>') {
      return 'rotate(-90deg)';
    } else if (arrowValue === '^') {
      return 'rotate(0deg)';
    } else if (arrowValue === 'v') {
      return 'rotate(180deg)';
    }
    return 'none';
  };

  const arrowStyles = {
    width: '20px',
    height: '20px',
    fill: '#f06d06', // Arrow color
    position: 'absolute',
    transformOrigin: 'center',
  };

  return (
    <div className="sudoku-cell" style={{ position: 'relative' }}>
      <Grid container direction="column">
        <div style={{ position: 'relative' }}>
          {up && (
            <KeyboardArrowUpIcon
              className="arrow-icon"
              style={{
                ...arrowStyles,
                top: '-10px',
                left: '50%',
                transform: `${getArrowIconRotation(up)} translateX(-50%)`,
              }}
            />
          )}
          {down && (
            <KeyboardArrowDownIcon
              className="arrow-icon"
              style={{
                ...arrowStyles,
                bottom: '-10px',
                left: '50%',
                transform: `${getArrowIconRotation(down)} translateX(-50%)`,
              }}
            />
          )}
        </div>
        <Grid item container justifyContent="space-between">
          <div style={{ position: 'relative' }}>
            {left && (
              <KeyboardArrowLeftIcon
                className="arrow-icon"
                style={{
                  ...arrowStyles,
                  left: '-10px',
                  top: '50%',
                  transform: `${getArrowIconRotation(left)} translateY(-50%)`,
                }}
              />
            )}
            {value}
            {right && (
              <KeyboardArrowRightIcon
                className="arrow-icon"
                style={{
                  ...arrowStyles,
                  right: '-10px',
                  top: '50%',
                  transform: `${getArrowIconRotation(right)} translateY(-50%)`,
                }}
              />
            )}
          </div>
        </Grid>
      </Grid>
    </div>
  );
};

export default ComparisonSudokuCell;
